const dialog = {
    display: 'inline-grid',
    height: '100vh',
    justifyContent: 'center',
    alignItems: 'center',
    float: 'left',
    paddingLeft: '13%'
}

const header = {
    fontSize: '6em',
    fontFamily: 'Source Sans Pro, sans-serif',
    fontWeight: '700',
    fontColor: 'grey',
    marginBottom: '0'
}

const caption = {
    width: '50%',
    height: '50px',
    fontSize: '1.2em',
    fontFamily: 'Source Sans Pro, sans-serif',
    fontWeight: '200'
